import React, { useState, useRef } from "react";
import "./ForgotPassword.scss";
import Overlay from "../Overlay/Overlay";

const PasswordResetCard = () => {
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");
  const [showOverlay, setShowOverlay] = useState(false);


  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const handlePasswordReset = (event) => {
    event.preventDefault();

    // Send a POST request to the PHP API to check if the user exists
    fetch("https://techytest23.000webhostapp.com/check-user-exists.php", {
      method: "POST",
      headers: {
        "Content-Type": "application/x-www-form-urlencoded",
      },
      body: `email=${email}`,
    })
      .then((response) => response.text())
      .then((data) => {
        if (data === "User exists") {
          // If the user exists, send an email to the user's email address
          fetch("https://techytest23.000webhostapp.com/send-password-reset-email.php", {
            method: "POST",
            headers: {
              "Content-Type": "application/x-www-form-urlencoded",
            },
            body: `email=${email}`,
          })
            .then((response) => response.text())
            .then((data) => {
              setMessage(data);
            })
            .catch((error) => console.error(error));
        } else {
          // If the user does not exist, show a warning message
          setMessage("User does not exist.");
        }
      })
      .catch((error) => console.error(error));
  };

   // Toggle the visibility via a useState setting the variable to False.
   const [showForm, setShowForm] = useState(false);
   //useRef is used to hold a reference which is stored in formRef (aka.).
   const formRef = useRef(null);

  const toggleForm = () => {
    setShowForm(!showForm);
    setShowOverlay(!showOverlay);
  };

  return (
    <div className="card password-reset-card">
    <Overlay onClick={toggleForm} />
      <div className="card-body">
        <h2 className="card-title">Forgot your password?</h2>
        <form onSubmit={handlePasswordReset}>
          <div className="form-group">
            <label htmlFor="email">Enter your email address:</label>
            <input
              type="email"
              className="form-control"
              id="email"
              value={email}
              onChange={handleEmailChange}
            />
          </div>
          <button type="submit" className="btn btn-primary btn-block">
            Confirm
          </button>
          <div className="message">{message}</div>
        </form>
      </div>
    </div>
  );
};

export default PasswordResetCard;
